sysauto_script.script:
// Get today's time and date
var rightNow0 = new GlideDateTime();
var Next24Hours = new GlideDateTime();
Next24Hours.addDaysLocalTime(1);
// Query the database for NeedIt Task records with Due date field values older
// than the current time.  Only return NeedIt Task records that do not have
// a State field value of Closed Complete.
var futureTasks = new GlideRecord('x_58872_needit_needit_task');
futureTasks.addQuery('due_date', '<=', Next24Hours);
futureTasks.addQuery('due_date', '>=', rightNow0);
futureTasks.addQuery('state', '!=', 3);
gs.info("NeedIt Task due soon = from " + rightNow0 + " to " +Next24Hours);

futureTasks.query();
// Write a log message for each overdue NeedIt Task Record
while (futureTasks.next()) {
    gs.info("Due soon NeedIt Task record = " + futureTasks.number);
	gs.eventQueue("x_58872_needit.dueSoonNeedItTasks",futureTasks,futureTasks.number,gs.getUserName());
}
